import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { searchDestination } from '../../state/actions/destinationActions';
import { selectSearchResults } from '../../state/selectors/destinationSelectors';

const SelectSearchResults = ({ destinations }) => {
  const dispatch = useDispatch();
  const searchResults = useSelector((selectSearchResults));
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    dispatch(searchDestination(searchTerm));
  }, [dispatch, searchTerm]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  return (
    <div>
      <input
        type="text"
        value={searchTerm}
        onChange={handleSearchChange}
        placeholder="Search destinations"
      />

      <ul>
        {searchResults && searchResults.map((destinations) => (
          <li key={destinations.name}>{destinations.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default SelectSearchResults;
