import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { Link } from 'react-router-dom';
import { deleteDestinationByID } from '../../state/actions/destinationActions';


const DestinationList = ({ }) => {
  const dispatch = useDispatch();
  const destinationList = useSelector((state) => state.destinations);

  const handleDelete = () => {
    dispatch(deleteDestinationByID());
  };

  return (
    <ul>
      {destinationList.map((destination) => (
        <React.Fragment key={destination.id}>
        <Link to={`/destinations/${destination.id}`}>
          <li>{destination.name}</li>
        </Link> 
        <button onClick={() => handleDelete(destination.id)}>Delete</button>
      </React.Fragment>
      ))}
    </ul>
  );
};

export default DestinationList;
