import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { fetchDestinations, searchDestination, deleteDestinationByID } from '../../state/actions/destinationActions';
import { selectDestinations, selectSearchResults } from '../../state/selectors/destinationSelectors';
import styles from '../../Global';

const DestinationSearchList = () => {
  const dispatch = useDispatch();
  const destinations = useSelector(selectDestinations);
  const searchResults = useSelector(selectSearchResults);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    dispatch(fetchDestinations());
  }, [dispatch]);

  useEffect(() => {
    dispatch(searchDestination(searchTerm));
  }, [dispatch, searchTerm]);

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredResults = searchResults.filter((destination) =>
    destination.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleDelete = (_id) => {
    dispatch(deleteDestinationByID(_id));
  };

  return (
    <div className={`${styles.maxWMd} ${styles.mxAuto} ${styles.bgWhite} ${styles.roundedXl} ${styles.shadowMd} ${styles.p4}`}>
      <h2 className={`${styles.textXl} ${styles.fontBold} ${styles.mb4}`}>Agregar</h2>
      <div className={`${styles.mb4}`}>
        <input
          type="text"
          value={searchTerm}
          onChange={handleSearchChange}
          placeholder="Search destinations"
          className={`w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500`}
        />
      </div>
      <ul>
        {filteredResults.map((destination) => (
          <li key={destination._id} className={`${styles.mb2}`}>
            <Link to={`/destinations/${destination._id}`} className="text-blue-500 hover:underline">
            {destination.name}
          </Link>
            <button
              onClick={() => handleDelete(destination._id)}
              className={`${styles.ml2} ${styles.px3} ${styles.py1} ${styles.textWhite} ${styles.bgRed500} ${styles.roundedMd} ${styles.hoverBgRed600}`}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default DestinationSearchList;
